import React, { useEffect, useState } from "react";
import axios from "axios";
import { useParams, useLocation } from "react-router-dom"; // Import useLocation to get search query from header
import jsPDF from "jspdf"; // Import jsPDF
import './clientDeeds.css'; // Assuming you'll have a CSS file for styling deeds
import Header from "../header";
import Footer from "../../components_home/Home_Footer";

function ClientDeeds() {
    const { nic } = useParams(); // Get NIC from the URL params
    const [deeds, setDeeds] = useState([]);
    const [filteredDeeds, setFilteredDeeds] = useState([]); // For storing filtered deeds
    const [error, setError] = useState('');
    const location = useLocation(); // Get search query from header
    const searchQuery = new URLSearchParams(location.search).get("q") || ""; // Extract the search query

    useEffect(() => {
        // Fetch deeds by NIC
        const fetchDeeds = async () => {
            try {
                const response = await axios.get(`http://localhost:8070/deeds/get_deeds_by_nic/${nic}`);
                setDeeds(response.data);
                filterDeeds(response.data); // Initial filter based on the search query
            } catch (err) {
                setError("Error fetching deeds. Please try again.");
                console.error(err);
            }
        };

        fetchDeeds();
    }, [nic]);

    // Function to filter deeds based on search query
    const filterDeeds = (deedsData) => {
        const filtered = deedsData.filter((deedData) => {
            return (
                deedData.deedNo.toString().includes(searchQuery) ||
                deedData.title.toLowerCase().includes(searchQuery.toLowerCase()) ||
                deedData.district.toLowerCase().includes(searchQuery.toLowerCase()) ||
                deedData.division.toLowerCase().includes(searchQuery.toLowerCase()) ||
                deedData.preRegisteredNo.toString().includes(searchQuery) ||  // Search by Pre-Registered No
                deedData.considerationValue.toString().includes(searchQuery) || // Search by Consideration Value
                deedData.lawyerFee.toString().includes(searchQuery) || // Search by Lawyer Fee
                deedData.taxFee.toString().includes(searchQuery) ||  // Search by Tax Fee
                deedData.totalFee.toString().includes(searchQuery) || // Search by Total Fee
                deedData.deedStatus.toLowerCase().includes(searchQuery.toLowerCase()) // Search by Status
            );
        });
        setFilteredDeeds(filtered);
    };

    // Function to filter deeds by status (for PDF generation)
    const filterByStatus = (status) => {
        return filteredDeeds.filter(deedData => deedData.deedStatus === status);
    };

    // Function to generate PDF report
    const generatePDF = () => {
        const doc = new jsPDF();
        doc.text("Deed Report", 20, 10);
        doc.text("Registered Deeds:", 20, 20);

        const registeredDeeds = filterByStatus('Registered');
        registeredDeeds.forEach((deedData, index) => {
            doc.text(`Deed Number: ${deedData.deedNo}, Title: ${deedData.title}`, 20, 30 + (index * 10));
        });

        doc.addPage();
        doc.text("Pending Deeds:", 20, 10);
        const pendingDeeds = filterByStatus('Pending');
        pendingDeeds.forEach((deedData, index) => {
            doc.text(`Deed Number: ${deedData.deedNo}, Title: ${deedData.title}`, 20, 30 + (index * 10));
        });

        doc.save("deed_report.pdf"); // Save the PDF
    };

    return (
        <div>
            <Header />
            <div className="deeds-container">
                <h1>Deeds</h1> <br/>
                {error && <p className="error-message">{error}</p>}
                {filteredDeeds.length > 0 ? (
                    <table className="deeds-table">
                        <thead>
                            <tr>
                                <th>Deed Number</th>
                                <th>Title</th>
                                <th>Pre-Registered No</th>
                                <th>District</th>
                                <th>Division</th>
                                <th>Consideration Value</th>
                                <th>Lawyer Fee</th>
                                <th>Tax Fee</th>
                                <th>Total Fee</th>
                                <th>Status</th>
                            </tr>
                        </thead>
                        <tbody>
                            {filteredDeeds.map((deedData) => (
                                <tr key={deedData._id}>
                                    <td>{deedData.deedNo}</td>
                                    <td>{deedData.title}</td>
                                    <td>{deedData.preRegisteredNo}</td>
                                    <td>{deedData.district}</td>
                                    <td>{deedData.division}</td>
                                    <td>{deedData.considerationValue}</td>
                                    <td>{deedData.lawyerFee}</td>
                                    <td>{deedData.taxFee}</td>
                                    <td>{deedData.totalFee}</td>
                                    <td>{deedData.deedStatus}</td>
                                </tr>
                            ))}
                        </tbody>
                    </table>
                ) : (
                    <p>No deeds found for this NIC or search query.</p>
                )}
                <button onClick={generatePDF} className="btn btn-primary">Generate Report</button> {/* Button to generate report */}
            </div>
            <Footer />
        </div>
    );
}

export default ClientDeeds;
