import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import './ClientPortalHome.css'; 
import Header from "../header";
import BalanceIcon from '@mui/icons-material/Balance';
import ReceiptLongIcon from '@mui/icons-material/ReceiptLong';
import axios from "axios";
import Footer from "../../components_home/Home_Footer";

function ClientPortalHome() {
    const [clientName, setClientName] = useState('');
    const [nic, setNic] = useState(''); // To store the NIC
    const [currentDate, setCurrentDate] = useState('');
    const [caseCount, setCaseCount] = useState(0); // To store the case count
    const [grantorDeedCount, setGrantorDeedCount] = useState(0); // Deeds as grantor
    const [granteeDeedCount, setGranteeDeedCount] = useState(0); // Deeds as grantee
    const [totalDeedCount, setTotalDeedCount] = useState(0); // Total deeds count

    // Messaging system state
    const [receivedMessages, setReceivedMessages] = useState([]);
    const [sentMessages, setSentMessages] = useState([]);
    const [newMessage, setNewMessage] = useState(''); // To store new message input

    const navigate = useNavigate();

    const handleContainerClick = (path) => {
        navigate(`${path}/${nic}`); // Pass NIC as a parameter in the path
    };

    // Fetch data when component mounts
    useEffect(() => {
        const today = new Date();
        const options = { weekday: 'long', year: 'numeric', month: 'long', day: 'numeric' };
        const formattedDate = today.toLocaleDateString(undefined, options);
        setCurrentDate(formattedDate);

        // Retrieve client name and NIC from localStorage
        const storedClientName = localStorage.getItem('userName');
        const storedNic = localStorage.getItem('userNic');

        setClientName(storedClientName || 'Client'); // Default to 'Client' if no name is found
        setNic(storedNic); // Set NIC

        // Fetch the case count by NIC
        const fetchCaseCount = async () => {
            try {
                const response = await axios.get(`http://localhost:8070/case/get_case_count_by_nic/${storedNic}`);
                setCaseCount(response.data.count);
            } catch (err) {
                console.error("Error fetching case count:", err);
            }
        };

        // Fetch the deed count by NIC (grantor and grantee separately)
        const fetchDeedCount = async () => {
            try {
                const response = await axios.get(`http://localhost:8070/deeds/get_deed_count_by_nic/${storedNic}`);
                setGrantorDeedCount(response.data.grantorCount);
                setGranteeDeedCount(response.data.granteeCount);
                setTotalDeedCount(response.data.totalCount); // Total deed count
            } catch (err) {
                console.error("Error fetching deed count:", err);
            }
        };

        // Fetch messages
        const fetchMessages = async () => {
            try {
                const response = await axios.get(`http://localhost:8070/message/get_messages_by_nic/${storedNic}`);
                setReceivedMessages(response.data.receivedMessages);
                setSentMessages(response.data.sentMessages);
            } catch (err) {
                console.error("Error fetching messages:", err);
            }
        };

        if (storedNic) {
            fetchCaseCount();
            fetchDeedCount(); // Fetch deed count after NIC is available
            fetchMessages(); // Fetch messages when NIC is available
        }
    }, [nic]);

    // Handle sending a new message
    const handleSendMessage = async () => {
        try {
            await axios.post("http://localhost:8070/messages/send_message", {
                nic,
                message: newMessage,
                date: new Date(),
            });
            setSentMessages([...sentMessages, { message: newMessage, date: new Date() }]);
            setNewMessage('');
        } catch (err) {
            console.error("Error sending message:", err);
        }
    };

    return (
        <div>
            <Header />

            <div className="welcome-tag">
                <h1>Client Portal Management</h1>
                <p>Today is {currentDate}</p>
                <p>Welcome, <h2>{clientName}!</h2></p>
            </div>

            <div className="main-container">
                <div
                    className="clickable-container"
                    onClick={() => handleContainerClick('/viewCase')}
                >
                    <BalanceIcon sx={{ fontSize: 80 }} /> <br /><br />
                    <p>Cases</p>
                </div>

                <div
                    className="clickable-container"
                    onClick={() => handleContainerClick('/viewDeed')}
                >
                    <ReceiptLongIcon sx={{ fontSize: 80 }} /> <br /><br />
                    <p>Deeds</p>
                </div>
            </div>

            <div className="case-count-container">
                <h2>Total Cases: {caseCount}</h2>
            </div>

            <div className="deed-count-container">
                <h2>Total Deeds: {totalDeedCount}</h2>
                <p>Grantee: {granteeDeedCount}</p>
                <p>Grantor: {grantorDeedCount}</p>
            </div>

            {/* Messaging System Section */}
            <br /><br />
            <div className="messaging-system-container">
                <h2>Messaging System</h2>
                <div className="messaging-box">
                    <div className="received-messages">
                        <h3>Received</h3>
                        {receivedMessages.length === 0 ? (
                            <p>No messages received.</p>
                        ) : (
                            receivedMessages.map((msg, index) => (
                                <p key={index}>{msg.message} - {new Date(msg.date).toLocaleString()}</p>
                            ))
                        )}
                    </div>
                    <div className="send-messages">
                        <h3>Send</h3>
                        {sentMessages.length === 0 ? (
                            <p>No messages sent.</p>
                        ) : (
                            sentMessages.map((msg, index) => (
                                <p key={index}>{msg.message} - {new Date(msg.date).toLocaleString()}</p>
                            ))
                        )}
                    </div>
                    <div className="send-message-input">
                        <textarea
                            value={newMessage}
                            onChange={(e) => setNewMessage(e.target.value)}
                            placeholder="Type your message here..."
                        />
                        <button onClick={handleSendMessage}>Send Message</button>
                    </div>
                </div>
            </div>
            <Footer />
        </div>
    );
}

export default ClientPortalHome;
