import React from "react";
import { useNavigate } from "react-router-dom"; // Import useNavigate for navigation
import './ClientPortalHome.css'; // Import the CSS file
import Header from "../header";

import BalanceIcon from '@mui/icons-material/Balance';
import ReceiptLongIcon from '@mui/icons-material/ReceiptLong';


function ClientPortalHome() {
    const navigate = useNavigate(); // Initialize useNavigate

    const handleContainerClick = (path) => {
        navigate(path); // Navigate to the specified path
    };

    return (
        <div>
            <Header />

            <h1>Client Portal Management</h1>

            <div className="main-container">
                <div
                    className="clickable-container"
                    onClick={() => handleContainerClick('#')} // Navigate to /clients

                >
                    <BalanceIcon />
                    <p>Cases</p>

                </div>
                <div
                    className="clickable-container"
                    onClick={() => handleContainerClick('#')} // Navigate to /settings
                >
                    <ReceiptLongIcon />
                    <p>Deeds</p>
                </div>
            </div>
        </div>
    );
}

export default ClientPortalHome;
