import React, { useEffect, useState } from "react";
import axios from "axios";
import { useParams, useLocation } from "react-router-dom";
import jsPDF from "jspdf";
import './clientDeeds.css';
import Header from "../header";
import Footer from "../../components_home/Home_Footer";

function ClientDeeds() {
    const { id } = useParams(); // Get client ID from the URL params (could be grantor or grantee)
    const [deeds, setDeeds] = useState([]);
    const [filteredDeeds, setFilteredDeeds] = useState([]);
    const [error, setError] = useState('');
    const location = useLocation();
    const searchQuery = new URLSearchParams(location.search).get("q") || "";

    useEffect(() => {
        // Fetch deeds by client ID (either grantor or grantee)
        const fetchDeeds = async () => {
            try {
                const response = await axios.get(`http://localhost:8070/deed/get_deeds_by_client_id/${id}`);
                setDeeds(response.data);
                filterDeeds(response.data); // Filter deeds initially based on search query
            } catch (err) {
                setError("Error fetching deeds. Please try again.");
                console.error(err);
            }
        };

        fetchDeeds();
    }, [id]);

    // Function to filter deeds based on search query
    const filterDeeds = (deedsData) => {
        const filtered = deedsData.filter((deedData) => {
            return (
                deedData.deedNo.toString().includes(searchQuery) ||
                deedData.title.toLowerCase().includes(searchQuery.toLowerCase()) ||
                deedData.district.toLowerCase().includes(searchQuery.toLowerCase()) ||
                deedData.deedStatus.toLowerCase().includes(searchQuery.toLowerCase())
            );
        });
        setFilteredDeeds(filtered);
    };

    // Function to filter deeds by status for PDF generation
    const filterByStatus = (status) => {
        return filteredDeeds.filter(deedData => deedData.deedStatus === status);
    };

    // Function to generate PDF report
    const generatePDF = () => {
        const doc = new jsPDF();
        doc.text("Deed Report", 20, 10);
        doc.text("Registered Deeds:", 20, 20);

        const registeredDeeds = filterByStatus('Registered');
        registeredDeeds.forEach((deedData, index) => {
            doc.text(`Deed Number: ${deedData.deedNo}, Title: ${deedData.title}`, 20, 30 + (index * 10));
        });

        doc.addPage();
        doc.text("Pending Deeds:", 20, 10);
        const pendingDeeds = filterByStatus('Pending');
        pendingDeeds.forEach((deedData, index) => {
            doc.text(`Deed Number: ${deedData.deedNo}, Title: ${deedData.title}`, 20, 30 + (index * 10));
        });

        doc.save("deed_report.pdf");
    };

    return (
        <div>
            <Header />
            <div className="deeds-container">
                <h1>Deeds</h1> <br/>
                {error && <p className="error-message">{error}</p>}
                {filteredDeeds.length > 0 ? (
                    <table className="deeds-table">
                        <thead>
                            <tr>
                                <th>Deed Number</th>
                                <th>Title</th>
                                <th>District</th>
                                <th>Division</th>
                                <th>Consideration Value</th>
                                <th>Lawyer Fee</th>
                                <th>Tax Fee</th>
                                <th>Total Fee</th>
                                <th>Deed Status</th>
                            </tr>
                        </thead>
                        <tbody>
                            {filteredDeeds.map((deedData) => (
                                <tr key={deedData._id}>
                                    <td>{deedData.deedNo}</td>
                                    <td>{deedData.title}</td>
                                    <td>{deedData.district}</td>
                                    <td>{deedData.division}</td>
                                    <td>{deedData.considerationValue}</td>
                                    <td>{deedData.lawyerFee}</td>
                                    <td>{deedData.taxFee}</td>
                                    <td>{deedData.totalFee}</td>
                                    <td>{deedData.deedStatus}</td>
                                </tr>
                            ))}
                        </tbody>
                    </table>
                ) : (
                    <p>No deeds found for this client or search query.</p>
                )}
                <button onClick={generatePDF} className="btn btn-primary">Generate Report</button>
            </div>
            <Footer />
        </div>
    );
}

export default ClientDeeds;
