import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import './ClientPortalHome.css'; 
import Header from "../header";
import BalanceIcon from '@mui/icons-material/Balance';
import ReceiptLongIcon from '@mui/icons-material/ReceiptLong';
import axios from "axios";
import Footer from "../../components_home/Home_Footer";

function ClientPortalHome() {
    const [clientName, setClientName] = useState('');
    const [nic, setNic] = useState(''); // To store the NIC
    const [currentDate, setCurrentDate] = useState('');
    const [caseCount, setCaseCount] = useState(0); // To store the case count
    const [deedCount, setDeedCount] = useState(0);
    const navigate = useNavigate();

    const handleContainerClick = (path) => {
        navigate(`${path}/${nic}`); // Pass NIC as a parameter in the path
    };

    useEffect(() => {
        const today = new Date();
        const options = { weekday: 'long', year: 'numeric', month: 'long', day: 'numeric' };
        const formattedDate = today.toLocaleDateString(undefined, options);
        setCurrentDate(formattedDate);

        // Retrieve client name and NIC from localStorage
        const storedClientName = localStorage.getItem('userName');
        const storedNic = localStorage.getItem('userNic');

        setClientName(storedClientName || 'Client'); // Default to 'Client' if no name is found
        setNic(storedNic); // Set NIC

        // Fetch the case count by NIC
        const fetchCaseCount = async () => {
            try {
                const response = await axios.get(`http://localhost:8070/case/get_case_count_by_nic/${storedNic}`);
                setCaseCount(response.data.count);
            } catch (err) {
                console.error("Error fetching case count:", err);
            }
        };

        if (storedNic) {
            fetchCaseCount();
        }

        // Fetch the deed count by NIC
        const fetchDeedCount = async () => {
            try {
                const response = await axios.get(`http://localhost:8070/deed/get_deed_count_by_nic/${storedNic}`);
                setDeedCount(response.data.count);
            } catch (err) {
                console.error("Error fetching deed count:", err);
            }
        };

        if (storedNic) {
            fetchCaseCount();
            fetchDeedCount(); // Fetch deed count after NIC is available
        }
    }, []);

    return (
        <div>
            <Header />

            <div className="welcome-tag">
                <h1>Client Portal Management</h1>
                <p>Today is {currentDate}</p>
                <p>Welcome, <h2>{clientName}!</h2></p>
            </div>

            <div className="main-container">
                <div
                    className="clickable-container"
                    onClick={() => handleContainerClick('/viewCase')}
                >
                    <BalanceIcon sx={{ fontSize: 80 }} /> <br /><br />
                    <p>Cases</p>
                </div>

                <div
                    className="clickable-container"
                    onClick={() => handleContainerClick('/viewDeed')}
                >
                    <ReceiptLongIcon sx={{ fontSize: 80 }} /> <br /><br />
                    <p>Deeds</p>
                </div>
            </div>

            <div className="case-count-container">
                <h2>Total Cases: {caseCount}</h2>
            </div>

            <div className="deed-count-container">
                <h2>Total deeds: {deedCount}</h2>
            </div>

            {/* Messaging System Section */}
            <br/><br/>
            <div className="messaging-system-container">
                <h2>Messaging System</h2>
                <div className="messaging-box">
                    <div className="received-messages">
                        <h3>Received</h3>
                        <p>No messages received.</p>
                    </div>
                    <div className="send-messages">
                        <h3>Send</h3>
                        <p>No messages sent.</p>
                    </div>
                </div>
            </div>
            <Footer />
        </div>
    );
}

export default ClientPortalHome;
