import React from "react";
import { useNavigate } from "react-router-dom"; // Import useNavigate for navigation
import './ClientPortalHome.css'; // Import the CSS file
import Header from "../header";

function ClientPortalHome() {
    const navigate = useNavigate(); // Initialize useNavigate

    const handleContainerClick = (path) => {
        navigate(path); // Navigate to the specified path
    };

    return (
        <div>
            <Header />

            <h1>Client Portal Management</h1>

            <div className="main-container">
                <div
                    className="clickable-container"
                    onClick={() => handleContainerClick('/profile')} // Navigate to /clients
                >
                    <p>Client Portal<br/>Login</p>
                </div>
                <div
                    className="clickable-container"
                    onClick={() => handleContainerClick('/view')} // Navigate to /settings
                >
                    <p>Client Portal<br/>Management</p>
                </div>
            </div>
        </div>
    );
}

export default ClientPortalHome;
