import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useParams } from 'react-router-dom';
import { useNavigate } from 'react-router-dom';
import Header from '../header';

const ClientProfile = () => {
    const { id } = useParams();
    const [client, setClient] = useState(null);

    const navigateUpdate = useNavigate();
    const handleUpdateClick = () => {
        navigateUpdate(`/update/${client._id}`);  
    }

    const navigateDelete = useNavigate();
    const handleDeleteClick = () => {
        navigateDelete(`/delete/${client._id}`);
    }

    useEffect(() => {
        const fetchClient = async () => {
            try {
                const response = await axios.get(`http://localhost:8070/client/get/${id}`);
                setClient(response.data.user);
            } catch (err) {
                console.error('Error fetching client data:', err);
            }
        };
        fetchClient();
    }, [id]);

    if (!client) {
        return <div>Loading...</div>;
    }

    return (
        <div>
            <Header />
            <h2>Profile</h2>
            <p><strong>First Name:</strong> {client.fname}</p>
            <p><strong>Last Name:</strong> {client.lname}</p>
            <p><strong>Email:</strong> {client.email}</p>
            <p><strong>Phone:</strong> {client.phone}</p>
            {/* Add more fields as necessary */}
            <button onClick={handleUpdateClick}>Update Profile</button>
            <button onClick={handleDeleteClick}>Delete Profile</button>
        </div>
    );
};

export default ClientProfile;
