import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import './ClientPortalHome.css'; 
import Header from "../header";
import BalanceIcon from '@mui/icons-material/Balance';
import ReceiptLongIcon from '@mui/icons-material/ReceiptLong';
import axios from 'axios';

function ClientPortalHome() {
    const [clientName, setClientName] = useState('');
    const [currentDate, setCurrentDate] = useState('');
    const [caseCount, setCaseCount] = useState(0); // State for case count
    const navigate = useNavigate();

    const handleContainerClick = (path) => {
        navigate(path);
    };

    // Get current date and client info
    useEffect(() => {
        const today = new Date();
        const options = { weekday: 'long', year: 'numeric', month: 'long', day: 'numeric' };
        const formattedDate = today.toLocaleDateString(undefined, options);
        setCurrentDate(formattedDate);

        // Fetch client name and NIC from localStorage
        const storedClientName = localStorage.getItem('clientName');
        const storedClientNIC = localStorage.getItem('clientNIC'); // Assuming you have stored NIC

        setClientName(storedClientName || 'Client'); // Fallback to 'Client' if no name is found

        // Fetch the count of cases by NIC
        const fetchCaseCount = async () => {
            try {
                const response = await axios.get(`http://localhost:8070/case/count_cases_by_nic/${storedClientNIC}`);
                setCaseCount(response.data.count);
            } catch (err) {
                console.error("Error fetching case count:", err);
            }
        };

        if (storedClientNIC) {
            fetchCaseCount();
        }
    }, []);

    return (
        <div>
            <Header />

            <div className="welcome-tag">
                <h1>Client Portal Management</h1>
                <p>Today is {currentDate}</p>
                <p>Welcome, {clientName}!</p>
            </div>

            <div className="case-count-container">
                <h2>You have {caseCount} active case(s) & {caseCount} active deeds(s).</h2> {/* Display the case count */}
            </div>

            <div className="main-container">
                <div
                    className="clickable-container"
                    onClick={() => handleContainerClick('/viewCase')}
                >
                    <BalanceIcon sx={{ fontSize: 80 }} /> <br /><br />
                    <p>Cases</p>
                </div>
                <div
                    className="clickable-container"
                    onClick={() => handleContainerClick('/viewDeed')}
                >
                    <ReceiptLongIcon sx={{ fontSize: 80 }} /> <br /><br />
                    <p>Deeds</p>
                </div>
            </div>
        </div>
    );
}

export default ClientPortalHome;
