import React, { useEffect, useState } from "react";
import axios from "axios";
import { useParams } from "react-router-dom";
import './clientDeeds.css'; 
import Header from "../header";
import Footer from "../../components_home/Home_Footer";

function ClientDeed() {

    const { nic } = useParams(); // Get NIC from the URL params
    const [cases, setCases] = useState([]);
    const [error, setError] = useState('');

    useEffect(() => {
        // Fetch cases by NIC
        const fetchCases = async () => {
            try {
                const response = await axios.get(`http://localhost:8070/case/get_cases_by_nic/${nic}`);
                setCases(response.data);
            } catch (err) {
                setError("Error fetching cases. Please try again.");
                console.error(err);
            }
        };

        fetchCases();
    }, [nic]);

    return(
        <div>
            <Header />
            <div className="cases-container">
                <h1>Deeds</h1> <br/>
                {error && <p className="error-message">{error}</p>}
                {cases.length > 0 ? (
                    <table className="cases-table">
                        <thead>
                            <tr>
                                <th>Deed Type</th>
                                <th>preRegisteredNo</th>
                                <th>NIC</th>
                                <th>Procedure</th>
                                <th>Court Area</th>
                                <th>Date</th>
                                <th>district</th>
                                <th>Status</th>
                                <th>lawyerFee</th>
                                <th>Tax fee</th>
                            </tr>
                        </thead>
                        <tbody>
                            {cases.map((caseData) => (
                                <tr key={caseData._id}>
                                    <td>{caseData.nature}</td>
                                    <td>{caseData.caseNumber}</td>
                                    <td>{caseData.nic}</td>
                                    <td>{caseData.procedure}</td>
                                    <td>{caseData.courtType}</td>
                                    <td>{caseData.date}</td>
                                    <td>{caseData.courtArea}</td>
                                    <td>{caseData.status}</td>
                                    <td>{caseData.monetaryValue}</td>
                                </tr>
                            ))}
                        </tbody>
                    </table>
                ) : (
                    <p>No cases found for this NIC.</p>
                )}
            </div>
            <Footer />
        </div>
    );
}
 export default ClientDeed;
