import React, { useEffect, useState } from "react";
import axios from "axios";
import { useParams } from "react-router-dom"; 
import jsPDF from "jspdf"; 
import './clientDeeds.css'; 
import Header from "../header";
import Footer from "../../components_home/Home_Footer";

function ClientDeeds() {
    const { nic } = useParams(); // Get NIC from URL params
    const [deeds, setDeeds] = useState([]); // State to store deeds
    const [loading, setLoading] = useState(true); // Loading state

    useEffect(() => {
        // Fetch deeds by NIC
        const fetchDeeds = async () => {
            try {
                const res = await axios.get(`/api/deeds/get_deeds_by_nic/${nic}`);
                setDeeds(res.data); // Set deeds to state
                setLoading(false); // Set loading to false
            } catch (error) {
                console.error("Error fetching deeds:", error);
                setLoading(false); // Set loading to false even on error
            }
        };
        fetchDeeds();
    }, [nic]);

    // Function to generate PDF
    const generatePDF = () => {
        const doc = new jsPDF();
        doc.text(`Deeds for Client NIC: ${nic}`, 10, 10);
        deeds.forEach((deed, index) => {
            doc.text(`${index + 1}. Deed No: ${deed.deedNo}, Title: ${deed.title}`, 10, 20 + index * 10);
        });
        doc.save(`Deeds_Report_${nic}.pdf`);
    };

    if (loading) {
        return <p>Loading deeds...</p>;
    }

    return (
        <div className="client-deeds">
            <Header />
            <div className="deeds-container">
                <h2>Deeds for Client NIC: {nic}</h2>
                {deeds.length > 0 ? (
                    <div>
                        <button onClick={generatePDF}>Download PDF</button>
                        <ul>
                            {deeds.map(deed => (
                                <li key={deed._id}>
                                    <strong>Deed No:</strong> {deed.deedNo} <br />
                                    <strong>Title:</strong> {deed.title} <br />
                                    <strong>District:</strong> {deed.district} <br />
                                    <strong>Status:</strong> {deed.deedStatus}
                                </li>
                            ))}
                        </ul>
                    </div>
                ) : (
                    <p>No deeds found for this client.</p>
                )}
            </div>
            <Footer />
        </div>
    );
}

export default ClientDeeds;
