import React, { useEffect, useState } from "react";
import axios from "axios";
import { useLocation } from "react-router-dom"; // To get the query parameters
import Header from "../header";
import Footer from "../../components_home/Home_Footer";

function SearchResults() {
    const [cases, setCases] = useState([]);
    const [deeds, setDeeds] = useState([]);
    const [error, setError] = useState('');
    const location = useLocation(); // Get search query from URL

    // Extract the search query from the URL
    const searchQuery = new URLSearchParams(location.search).get("q") || "";

    useEffect(() => {
        // Fetch both cases and deeds based on the search query
        const fetchSearchResults = async () => {
            try {
                const response = await axios.get(`http://localhost:8070/api/search/search_all?q=${searchQuery}`);
                setCases(response.data.cases);
                setDeeds(response.data.deeds);
            } catch (err) {
                setError("Error fetching search results. Please try again.");
                console.error(err);
            }
        };

        fetchSearchResults();
    }, [searchQuery]);

    return (
        <div>
            <Header />
        <div className="search-results-container">
            <h1>Search Results for "{searchQuery}"</h1>

            {error && <p className="error-message">{error}</p>}

            {/* Display cases */}
            {cases.length > 0 && (
                <div>
                    <h2>Cases</h2>
                    <table className="search-results-table">
                        <thead>
                            <tr>
                                <th>Case Number</th>
                                <th>NIC</th>
                                <th>Status</th>
                            </tr>
                        </thead>
                        <tbody>
                            {cases.map((caseItem) => (
                                <tr key={caseItem._id}>
                                    <td>{caseItem.caseNumber}</td>
                                    <td>{caseItem.nic}</td>
                                    <td>{caseItem.status}</td>
                                </tr>
                            ))}
                        </tbody>
                    </table>
                </div>
            )}

            {/* Display deeds */}
            {deeds.length > 0 && (
                <div>
                    <h2>Deeds</h2>
                    <table className="search-results-table">
                        <thead>
                            <tr>
                                <th>Title</th>
                                <th>Status</th>
                            </tr>
                        </thead>
                        <tbody>
                            {deeds.map((deed) => (
                                <tr key={deed._id}>
                                    <td>{deed.title}</td>
                                    <td>{deed.deedStatus}</td>
                                </tr>
                            ))}
                        </tbody>
                    </table>
                </div>
            )}

            {/* If no results are found */}
            {cases.length === 0 && deeds.length === 0 && !error && (
                <p>No results found for "{searchQuery}".</p>
            )}
        </div>
        <Footer />
        </div>
    );
}

export default SearchResults;
